    private void buywatermelonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buywatermelonActionPerformed
        if(money>=25){
        nameplant form = new nameplant(this,true);
        form.setVisible(true);
        if(form.getSignal()=="ok"){
        w = new watermelon();
        w.setName(form.getName());
        name.setText(w.getName());
        foodlevel.setText(Integer.toString(w.getFood()));
        waterlevel.setText(Integer.toString(w.getWater()));
        lightlevel.setText(Integer.toString(w.getLight()));
        lblother.setText("Radius:");
        other.setText(Integer.toString(w.getRadius()));
        li.add(w);
        money-=25;
        
        }
        }
        else JOptionPane.showMessageDialog(this,"You do not have enough money");
    }//GEN-LAST:event_buywatermelonActionPerformed

    private void buylightActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buylightActionPerformed
        if(money>=5){
            light++;
            lightamt.setText(Integer.toString(light));
            money-=5;
            lblmoney.setText(Integer.toString(money)+"$");
        }
        else JOptionPane.showMessageDialog(this,"You do not have enough money");
    }//GEN-LAST:event_buylightActionPerformed

    private void buyfoodActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buyfoodActionPerformed
        if(money>=5){
            food++;
            foodamt.setText(Integer.toString(food));
            money-=5;
            lblmoney.setText(Integer.toString(money)+"$");
        }
        else JOptionPane.showMessageDialog(this,"You do not have enough money");
    }//GEN-LAST:event_buyfoodActionPerformed

