    private void buytomatoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buytomatoActionPerformed
        nameplant form = new nameplant(this,true);
        form.setVisible(true);
        if(form.getSignal()=="ok"){
        t = new tomato();
        t.setName(form.getName());
        name.setText(t.getName());
        foodlevel.setText(Integer.toString(t.getFood()));
        waterlevel.setText(Integer.toString(t.getWater()));
        lightlevel.setText(Integer.toString(t.getLight()));
        lblother.setText("Tomatoes:");
        other.setText(Integer.toString(t.getTomatoes()));
        li.add(t);
        }
    }//GEN-LAST:event_buytomatoActionPerformed

